  top: -9999px;
  left: -9999px;
  opacity: 0;
}

.nav__toggle {
  display: none;

  span {
    width: 20px;
    height: 2px;
    display: block;
    margin-bottom: 3px;
    background-color: $color-primary;
    transition: .3s all ease-in-out;

    &:last-child {
      margin-bottom: 0;
    }
  }
}

//Mobile
@media(max-width: 855px) {
  .header__body {
    padding: 0 10px;
    overflow: hidden;
  }
  .header__title {
    margin-top: 60px;
  }
  .header__button {
    padding: 22px;
    width: 100%;
  }
  .header__image {
    width: 200%;
  }
  .nav__toggle {
    display: block;
  }
  .nav__list {
    position: absolute;
    left: 0;
    top: 95px;
    flex-direction: column;
    background-color: white;
    width: 100%;
    display: none;
  }
  .nav__link {
    padding: 14px 20px;
    margin-left: 0;
  }
  .nav__link:hover {
    padding: 14px 20px;
  }
  .nav__btn:checked {
    ~ .nav__list {
      display: flex;
    }

    ~ .nav__toggle {
      span {
        &:nth-child(1) {
          transform: rotate(45deg) translate(2px, 5px);
        }
        &:nth-child(2) {
          opacity: 0;
        }
        &:nth-child(3) {
          transform: rotate(-45deg) translate(2px, -5px);
        }
      }
    }
  }
}

