/*Why us*/
.why-us {
  text-align: center;
}
.why-us__title {
  margin: 100px 0;
}

.why-us__items {
  display: flex;
  align-items: flex-start;
  justify-content: center;
}

.why-us__item {
  max-width: 160px;
  margin: 0 90px 100px 90px;
  display: flex;
  flex-direction: column;
  align-items: center;
}

.why-us__circle {
  content: '';
  height: 63px;
  width: 63px;
  border-radius: 50%;
  position: relative;
}

.why-us__item:nth-of-type(1) .why-us__circle {
  background-color: $color-redish;
}

.why-us__item:nth-of-type(2) .why-us__circle {
  background-color: $color-primary;
}

.why-us__item:nth-of-type(3) .why-us__circle {
  background-color: $color-green;
}

.why-us__icon {
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translateY(-50%);
}

.why-us__number {
  font-family: $font-secondary;
  margin: 15px 0 20px 0;
  font-size: 20px;
  font-weight: 800;
  letter-spacing: 5.96px;
}

.why-us__description {
  font-family: $font-secondary;
  font-size: 17px;
  color: $color-grey;
}

//Mobile
@media(max-width: 855px) {
  .why-us__title {
    margin: 60px 0;
  }
  .why-us__items {
    flex-direction: column;
    align-items: center;
  }
  .why-us__item {
    margin-bottom: 50px;
  }
}
